service-task-definition.script:
//// == 自動工程コンフィグの参照 / Config Retrieving ==
var scopeGroupId = parseInt( configs.get("conf_ScopeGroup") );
var postMessage = configs.get("conf_Message");
var dataIdC = configs.get( "conf_DataIdC" );

//// == ワークフローデータの参照 / Data Retrieving ==
var files = data.get( dataIdC );

//// == 演算 / Calculating ==
var openChat = feedService.begin();
var qgroup = qgroupDao.findById( scopeGroupId );
openChat.setShareQgroup( qgroup );
openChat.setMessage( postMessage );
if( files !== null ){
  for( var i = 0; i < files.size(); i++  ){
    openChat.attachFile( files.get(i) );
  }
}
openChat.post();
